import React, { useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';
import axios from 'axios';

const RECAPTCHA_SITE_KEY = '6LcPDUEqAAAAANpN7kmJemGWMP9deq2R57Ouw9JG';

const App = () => {
  const [captchaToken, setCaptchaToken] = useState(null);

  const handleRecaptcha = (token) => {
    console.log('Recaptcha token:', token); // Debug token
    setCaptchaToken(token);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting with token:', captchaToken); // Debug token
    if (captchaToken) {
      try {
        const response = await axios.post('http://localhost:5000/verify-captcha', { token: captchaToken });
        console.log('Server response:', response.data);
      } catch (error) {
        console.error('Error:', error);
      }
    } else {
      console.error('No captcha token found');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <ReCAPTCHA
          sitekey={RECAPTCHA_SITE_KEY}
          onChange={handleRecaptcha}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default App;
